import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

import Me1 from './../../img/about/Me1.jpg'
import Me2 from './../../img/about/Me2.jpg'
import Me3 from './../../img/about/Me3.jpg'
import Me4 from './../../img/about/Me4.jpg'

function SimpleSlider () {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 3,
    initialSlide: 0,
    responsive : [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 760,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2
      }
    },
    {
      breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 3
    }
  }
    ]
  };

  return (
    <div className='container'>
    <Slider {...settings}>
      <div className='imageMe'>
        <img src={Me1} alt='' />
      </div>
      <div className='imageMe'>
        <img src={Me2} alt='' />
      </div>
      <div className='imageMe'>
        <img src={Me3} alt='' />
      </div>
      <div className='imageMe'>
        <img src={Me4} alt='' />
      </div>
    </Slider>
    </div>
  );
};

export default SimpleSlider;
